00290 Edge *buildGraph(Graph *graphs, Edge *edges){
00291     srand(time(NULL));
00292 
00293     int numVertices = getLinkedListLength(graphs);
00294     int maxPossibleEdges = numVertices * (numVertices - 1) / 2;
00295     int rnd = generateRandomNumber(numVertices - 1, maxPossibleEdges);
00296 
00297     return edges = generateRandomGraph(graphs, edges, numVertices, rnd);
